import React, { createContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    // Check for a token in localStorage on initial load
    const token = localStorage.getItem('token');
    if (token) {
      // If token exists, set it as the default Authorization header
      api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      // You would typically fetch user data here, but for now we'll decode from token
      // For simplicity, we'll just assume the user is logged in if a token exists.
      // A robust app would verify the token with the backend.
      const storedUser = JSON.parse(localStorage.getItem('user'));
      if(storedUser) {
        setUser(storedUser);
      }
    }
    setLoading(false);
  }, []);

  const login = async (email, password) => {
    try {
      const response = await api.post('/api/auth/login', { email, password });
      const { token, user } = response.data;

      // Store token and user data
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(user));

      // Set the Authorization header for future requests
      api.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      setUser(user);
      navigate('/dashboard');
    } catch (error) {
      console.error('Login failed:', error.response ? error.response.data : error.message);
      // You should show an error message to the user here
      alert('Login failed. Please check your credentials.');
    }
  };

  const logout = () => {
    // Clear user data and token
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    delete api.defaults.headers.common['Authorization'];
    navigate('/login');
  };

  return (
    <AuthContext.Provider value={{ user, login, logout, loading }}>
      {!loading && children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
