import React, { useState, useEffect, useContext } from 'react';
import api from '../services/api';
import AuthContext from '../context/AuthContext';
import CreateShipmentModal from '../components/CreateShipmentModal';
import UpdateShipmentModal from '../components/UpdateShipmentModal';

const ShipmentsPage = () => {
  const [shipments, setShipments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useContext(AuthContext);

  // State for modals
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
  const [selectedShipment, setSelectedShipment] = useState(null);

  const fetchShipments = async () => {
    if (!user) return;
    try {
      setLoading(true);
      const response = await api.get('/api/shipments');
      setShipments(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch shipments. Please try again later.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchShipments();
  }, [user]);

  const handleCreateShipment = async (newShipmentData) => {
    try {
      const response = await api.post('/api/shipments', newShipmentData);
      setShipments([response.data, ...shipments]);
      setIsCreateModalOpen(false);
    } catch (err) {
      alert('Failed to create shipment. Please check the details and try again.');
      console.error(err);
    }
  };

  const handleUpdateShipment = async (shipmentId, updateData) => {
    try {
      const response = await api.put(`/api/shipments/${shipmentId}`, updateData);
      // Find the updated shipment in the list and replace it
      setShipments(shipments.map(s => s.id === shipmentId ? response.data : s));
      setIsUpdateModalOpen(false);
      setSelectedShipment(null);
    } catch (err) {
      alert('Failed to update shipment. Please try again.');
      console.error(err);
    }
  };

  const openUpdateModal = (shipment) => {
    setSelectedShipment(shipment);
    setIsUpdateModalOpen(true);
  };

  if (loading) return <div className="text-center mt-10">Loading shipments...</div>;
  if (error) return <div className="text-center mt-10 text-red-500">{error}</div>;

  return (
    <>
      <CreateShipmentModal 
        isOpen={isCreateModalOpen}
        onClose={() => setIsCreateModalOpen(false)}
        onShipmentCreated={handleCreateShipment}
      />
      <UpdateShipmentModal
        isOpen={isUpdateModalOpen}
        onClose={() => setIsUpdateModalOpen(false)}
        shipment={selectedShipment}
        onShipmentUpdated={handleUpdateShipment}
      />
      <div className="max-w-7xl mx-auto bg-white p-8 rounded-lg shadow-lg">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">Manage Shipments</h1>
          <button 
            onClick={() => setIsCreateModalOpen(true)}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
          >
            + Create Shipment
          </button>
        </div>
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white">
            <thead className="bg-gray-200">
              <tr>
                <th className="py-3 px-4 text-left">Tracking #</th>
                <th className="py-3 px-4 text-left">Destination</th>
                <th className="py-3 px-4 text-left">Status</th>
                <th className="py-3 px-4 text-left">Est. Delivery</th>
                <th className="py-3 px-4 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {shipments.length > 0 ? (
                shipments.map((shipment) => (
                  <tr key={shipment.id} className="border-b hover:bg-gray-50">
                    <td className="py-3 px-4 font-mono text-sm text-blue-600">{shipment.tracking_number}</td>
                    <td className="py-3 px-4">{shipment.destination_address}</td>
                    <td className="py-3 px-4">
                      <span className={`px-2 py-1 text-xs font-semibold rounded-full ${
                        shipment.status === 'delivered' ? 'bg-green-200 text-green-800' :
                        shipment.status === 'in_transit' ? 'bg-yellow-200 text-yellow-800' :
                        shipment.status === 'delayed' ? 'bg-red-200 text-red-800' :
                        'bg-gray-200 text-gray-800'
                      }`}>
                        {shipment.status.replace('_', ' ').toUpperCase()}
                      </span>
                    </td>
                    <td className="py-3 px-4">{new Date(shipment.estimated_delivery).toLocaleDateString()}</td>
                    <td className="py-3 px-4">
                      <button 
                        onClick={() => openUpdateModal(shipment)}
                        className="text-green-600 hover:underline"
                      >
                        Update
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" className="py-6 px-4 text-center text-gray-500">
                    No shipments found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default ShipmentsPage;
