import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);

  return (
    <nav className="bg-white shadow-md">
      <div className="container mx-auto px-6 py-3">
        <div className="flex items-center justify-between">
          <div className="text-xl font-semibold text-gray-700">
            <Link to="/" className="text-gray-800 hover:text-blue-600">LogisticsCo</Link>
          </div>
          <div className="flex items-center">
            <Link to="/track" className="px-3 py-2 text-gray-700 hover:text-blue-600">Track Shipment</Link>
            {user && (
              <>
                <Link to="/dashboard" className="px-3 py-2 text-gray-700 hover:text-blue-600">Dashboard</Link>
                <Link to="/shipments" className="px-3 py-2 text-gray-700 hover:text-blue-600">Shipments</Link>
              </>
            )}
          </div>
          <div className="flex items-center">
            {user ? (
              <>
                <span className="text-gray-700 mr-4">Welcome, {user.fullName}</span>
                <button
                  onClick={logout}
                  className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700"
                >
                  Logout
                </button>
              </>
            ) : (
              <Link to="/login" className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700">
                Login
              </Link>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
