import React, { useState, useEffect } from 'react';
import Modal from './Modal';
import api from '../services/api'; // Import api service

const CreateShipmentModal = ({ isOpen, onClose, onShipmentCreated }) => {
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [estDelivery, setEstDelivery] = useState('');
  const [clientId, setClientId] = useState(''); // State for the selected client
  const [clients, setClients] = useState([]); // State to store the list of clients
  const [error, setError] = useState('');

  // Fetch the list of clients when the modal is opened
  useEffect(() => {
    if (isOpen) {
      const fetchClients = async () => {
        try {
          const response = await api.get('/api/users/clients');
          setClients(response.data);
        } catch (err) {
          console.error("Failed to fetch clients", err);
          setError("Could not load the client list.");
        }
      };
      fetchClients();
    }
  }, [isOpen]); // Re-run this effect whenever `isOpen` changes

  const handleSubmit = (e) => {
    e.preventDefault();
    // Now also check if a client has been selected
    if (!origin || !destination || !estDelivery || !clientId) {
      setError('All fields are required, including selecting a client.');
      return;
    }
    setError('');
    
    const newShipmentData = {
      origin_address: origin,
      destination_address: destination,
      estimated_delivery: estDelivery,
      client_id: clientId, // Pass the selected client's ID
    };

    onShipmentCreated(newShipmentData);
  };

  return (
    <Modal isOpen={isOpen} onClose={onClose} title="Create New Shipment">
      <form onSubmit={handleSubmit}>
        {error && <p className="p-3 bg-red-100 text-red-700 rounded-lg mb-4">{error}</p>}
        <div className="space-y-4">
          {/* Client Selection Dropdown */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="client">
              Assign to Client
            </label>
            <select
              id="client"
              value={clientId}
              onChange={(e) => setClientId(e.target.value)}
              className="w-full px-4 py-2.5 border border-gray-300 rounded-lg bg-white focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
              required
            >
              <option value="" disabled>-- Select a Client --</option>
              {clients.map(client => (
                <option key={client.id} value={client.id}>
                  {client.full_name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="origin">
              Origin Address
            </label>
            <input
              id="origin"
              type="text"
              value={origin}
              onChange={(e) => setOrigin(e.target.value)}
              className="w-full px-4 py-2.5 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
              placeholder="e.g., 123 Main St, Anytown, USA"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="destination">
              Destination Address
            </label>
            <input
              id="destination"
              type="text"
              value={destination}
              onChange={(e) => setDestination(e.target.value)}
              className="w-full px-4 py-2.5 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
              placeholder="e.g., 456 Oak Ave, Sometown, USA"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1" htmlFor="estDelivery">
              Estimated Delivery Date
            </label>
            <input
              id="estDelivery"
              type="date"
              value={estDelivery}
              onChange={(e) => setEstDelivery(e.target.value)}
              className="w-full px-4 py-2.5 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
              required
            />
          </div>
        </div>
        <div className="flex justify-end mt-6">
          <button
            type="button"
            onClick={onClose}
            className="mr-3 px-5 py-2.5 bg-gray-200 text-gray-800 font-semibold rounded-lg hover:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-gray-400"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="px-5 py-2.5 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            Create Shipment
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default CreateShipmentModal;
