import React, { useState, useEffect } from 'react';
import Modal from './Modal';

const UpdateShipmentModal = ({ isOpen, onClose, shipment, onShipmentUpdated }) => {
  const [status, setStatus] = useState('');
  const [location, setLocation] = useState('');
  const [updateMessage, setUpdateMessage] = useState('');
  const [error, setError] = useState('');

  // When the modal opens, pre-fill the form with the current shipment's status
  useEffect(() => {
    if (shipment) {
      setStatus(shipment.status);
    }
  }, [shipment]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!status || !location || !updateMessage) {
      setError('All fields are required.');
      return;
    }
    setError('');
    
    const updateData = {
      status: status,
      location: location,
      status_update_message: updateMessage,
    };

    onShipmentUpdated(shipment.id, updateData);
  };
  
  // Don't render the modal if there's no shipment data
  if (!shipment) return null;

  return (
    <Modal isOpen={isOpen} onClose={onClose} title={`Update #${shipment.tracking_number}`}>
      <form onSubmit={handleSubmit}>
        {error && <p className="text-red-500 mb-4">{error}</p>}
        
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="status">
            Shipment Status
          </label>
          <select
            id="status"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            className="w-full px-4 py-2 border rounded-md bg-white focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="pending">Pending</option>
            <option value="in_transit">In Transit</option>
            <option value="delivered">Delivered</option>
            <option value="delayed">Delayed</option>
          </select>
        </div>

        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="location">
            Current Location
          </label>
          <input
            id="location"
            type="text"
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="e.g., Phnom Penh Sorting Facility"
            required
          />
        </div>

        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="updateMessage">
            Update Message
          </label>
          <input
            id="updateMessage"
            type="text"
            value={updateMessage}
            onChange={(e) => setUpdateMessage(e.target.value)}
            className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="e.g., Departed from facility"
            required
          />
        </div>

        <div className="flex justify-end">
          <button
            type="button"
            onClick={onClose}
            className="mr-4 px-4 py-2 bg-gray-300 text-gray-800 rounded-md hover:bg-gray-400"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="px-4 py-2 bg-green-600 text-white font-bold rounded-md hover:bg-green-700"
          >
            Update Status
          </button>
        </div>
      </form>
    </Modal>
  );
};

export default UpdateShipmentModal;
